import React, { Component, useEffect, useState } from "react";
import ReactDOM from "react-dom";
import Section from "./Section";
import axios from 'axios';
import "./styles.css";
import Quadrant from "./Quadrant";

const apiKey = process.env.REACT_APP_API_KEY;
const searchTerm = "art";
const fetchUrl = `https://api.giphy.com/v1/gifs/search?q=${searchTerm}&limit=50&api_key=${apiKey}`;


const ArtApi = () => {
	// state = {
	// 	apiData: [],
	// };
	const [firstAPI, setFirstAPI] = useState([]);
	const [secondAPI, setSecondAPI] = useState([]);
	const fetchFirstAPI = async() =>{
		// const response = await axios.get('https://aws.random.cat/meow')
		fetch('https://aws.random.cat/meow')
			.then((res) => res.json())
			.then((json) => setFirstAPI(response.data.file));
		// setFirstAPI(response.data.file)
	}
	const fetchSecondAPI = async() =>{
		const response = await axios.get('https://random.dog/woof.json')
		setFirstAPI(response.data.file)
	}
	useEffect(async()=>{
		fetchSecondAPI()
		fetchFirstAPI()
	},[])
	// componentDidMount() {
	// 	fetch(fetchUrl)
	// 		.then((res) => res.json())
	// 		.then((json) => this.setState({ apiData: json.data }));
	// }

	return (
		<div className="main">
			{/* <Section apiData={this.state.apiData} />
			<Section apiData={this.state.apiData} />
			<Section apiData={this.state.apiData} />
			<Section apiData={this.state.apiData} /> */}
			<Quadrant image={firstAPI}/>
			{/* <Quadrant image={secondAPI}/> */}
		</div>
	);
}

export default ArtApi;

const rootElement = document.getElementById("root");
ReactDOM.render(<ArtApi />, rootElement);
